function teams_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('teams');
	if(!$arrPerm[1]){
		return false;
	}

	$data['team_name'] = makeSafe($_REQUEST['team_name']);
		if($data['team_name'] == empty_lookup_value){ $data['team_name'] = ''; }
	$data['school'] = makeSafe($_REQUEST['school']);
		if($data['school'] == empty_lookup_value){ $data['school'] = ''; }
	$data['matches'] = makeSafe($_REQUEST['matches']);
		if($data['matches'] == empty_lookup_value){ $data['matches'] = ''; }
	$data['wins'] = makeSafe($_REQUEST['wins']);
		if($data['wins'] == empty_lookup_value){ $data['wins'] = ''; }
	$data['losses'] = makeSafe($_REQUEST['losses']);
		if($data['losses'] == empty_lookup_value){ $data['losses'] = ''; }
	$data['draws'] = makeSafe($_REQUEST['draws']);
		if($data['draws'] == empty_lookup_value){ $data['draws'] = ''; }
	$data['points'] = makeSafe($_REQUEST['points']);
		if($data['points'] == empty_lookup_value){ $data['points'] = ''; }
	$data['created_by'] = parseCode('<%%creatorUsername%%>', true);
	$data['team_logo'] = PrepareUploadedFile('team_logo', 102400,'jpg|jpeg|gif|png', false, '');
	if($data['team_logo']) createThumbnail($data['team_logo'], getThumbnailSpecs('teams', 'team_logo', 'tv'));
	if($data['team_logo']) createThumbnail($data['team_logo'], getThumbnailSpecs('teams', 'team_logo', 'dv'));
	if($data['team_name']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Team Name': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['school']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'School': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['matches'] == '') $data['matches'] = "0";
	if($data['wins'] == '') $data['wins'] = "0";
	if($data['losses'] == '') $data['losses'] = "0";
	if($data['draws'] == '') $data['draws'] = "0";
	if($data['points'] == '') $data['points'] = "0";

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from teams where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['team_logo']) $data['team_logo'] = makeSafe($row['team_logo']);
		}
	}

	// hook: teams_before_insert
	if(function_exists('teams_before_insert')){
		$args=array();
		if(!teams_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `teams` set       ' . ($data['team_logo'] != '' ? "`team_logo`='{$data['team_logo']}'" : '`team_logo`=NULL') . ', `team_name`=' . (($data['team_name'] !== '' && $data['team_name'] !== NULL) ? "'{$data['team_name']}'" : 'NULL') . ', `school`=' . (($data['school'] !== '' && $data['school'] !== NULL) ? "'{$data['school']}'" : 'NULL') . ', `matches`=' . (($data['matches'] !== '' && $data['matches'] !== NULL) ? "'{$data['matches']}'" : 'NULL') . ', `wins`=' . (($data['wins'] !== '' && $data['wins'] !== NULL) ? "'{$data['wins']}'" : 'NULL') . ', `losses`=' . (($data['losses'] !== '' && $data['losses'] !== NULL) ? "'{$data['losses']}'" : 'NULL') . ', `draws`=' . (($data['draws'] !== '' && $data['draws'] !== NULL) ? "'{$data['draws']}'" : 'NULL') . ', `points`=' . (($data['points'] !== '' && $data['points'] !== NULL) ? "'{$data['points']}'" : 'NULL') . ', `created_by`=' . "'{$data['created_by']}'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"teams_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: teams_after_insert
	if(function_exists('teams_after_insert')){
		$res = sql("select * from `teams` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!teams_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('teams', $recID, getLoggedMemberID());

	return $recID;
}

function teams_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
	// insure referential integrity ...
	global $Translation;
	$selected_id=makeSafe($selected_id);

	// mm: can member delete record?
	$arrPerm=getTablePermissions('teams');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='teams' and pkValue='$selected_id'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='teams' and pkValue='$selected_id'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: teams_before_delete
	if(function_exists('teams_before_delete')){
		$args=array();
		if(!teams_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	// child table: students
	$res = sql("select `id` from `teams` where `id`='$selected_id'", $eo);
	$id = db_fetch_row($res);
	$rires = sql("select count(1) from `students` where `team`='".addslashes($id[0])."'", $eo);
	$rirow = db_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "students", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$skipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "students", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=\"button\" class=\"button\" value=\"".$Translation['yes']."\" onClick=\"window.location='teams_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=\"button\" class=\"button\" value=\"".$Translation['no']."\" onClick=\"window.location='teams_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}

	sql("delete from `teams` where `id`='$selected_id'", $eo);

	// hook: teams_after_delete
	if(function_exists('teams_after_delete')){
		$args=array();
		teams_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='teams' and pkValue='$selected_id'", $eo);
}

function teams_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('teams');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='teams' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='teams' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['team_name'] = makeSafe($_REQUEST['team_name']);
		if($data['team_name'] == empty_lookup_value){ $data['team_name'] = ''; }
	if($data['team_name']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Team Name': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['school'] = makeSafe($_REQUEST['school']);
		if($data['school'] == empty_lookup_value){ $data['school'] = ''; }
	if($data['school']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'School': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['matches'] = makeSafe($_REQUEST['matches']);
		if($data['matches'] == empty_lookup_value){ $data['matches'] = ''; }
	$data['wins'] = makeSafe($_REQUEST['wins']);
		if($data['wins'] == empty_lookup_value){ $data['wins'] = ''; }
	$data['losses'] = makeSafe($_REQUEST['losses']);
		if($data['losses'] == empty_lookup_value){ $data['losses'] = ''; }
	$data['draws'] = makeSafe($_REQUEST['draws']);
		if($data['draws'] == empty_lookup_value){ $data['draws'] = ''; }
	$data['points'] = makeSafe($_REQUEST['points']);
		if($data['points'] == empty_lookup_value){ $data['points'] = ''; }
	$data['edited_by'] = parseCode('<%%editorUsername%%>', false);
	$data['selectedID']=makeSafe($selected_id);
	if($_REQUEST['team_logo_remove'] == 1){
		$data['team_logo'] = '';
	}else{
		$data['team_logo'] = PrepareUploadedFile('team_logo', 102400, 'jpg|jpeg|gif|png', false, "");
		if($data['team_logo']) createThumbnail($data['team_logo'], getThumbnailSpecs('teams', 'team_logo', 'tv'));
		if($data['team_logo']) createThumbnail($data['team_logo'], getThumbnailSpecs('teams', 'team_logo', 'dv'));
	}

	// hook: teams_before_update
	if(function_exists('teams_before_update')){
		$args=array();
		if(!teams_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `teams` set       ' . ($data['team_logo']!='' ? "`team_logo`='{$data['team_logo']}'" : ($_REQUEST['team_logo_remove'] != 1 ? '`team_logo`=`team_logo`' : '`team_logo`=NULL')) . ', `team_name`=' . (($data['team_name'] !== '' && $data['team_name'] !== NULL) ? "'{$data['team_name']}'" : 'NULL') . ', `school`=' . (($data['school'] !== '' && $data['school'] !== NULL) ? "'{$data['school']}'" : 'NULL') . ', `matches`=' . (($data['matches'] !== '' && $data['matches'] !== NULL) ? "'{$data['matches']}'" : 'NULL') . ', `wins`=' . (($data['wins'] !== '' && $data['wins'] !== NULL) ? "'{$data['wins']}'" : 'NULL') . ', `losses`=' . (($data['losses'] !== '' && $data['losses'] !== NULL) ? "'{$data['losses']}'" : 'NULL') . ', `draws`=' . (($data['draws'] !== '' && $data['draws'] !== NULL) ? "'{$data['draws']}'" : 'NULL') . ', `points`=' . (($data['points'] !== '' && $data['points'] !== NULL) ? "'{$data['points']}'" : 'NULL') . ', `edited_by`=' . "'{$data['edited_by']}'" . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="teams_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: teams_after_update
	if(function_exists('teams_after_update')){
		$res = sql("SELECT * FROM `teams` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!teams_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='teams' and pkValue='".makeSafe($selected_id)."'", $eo);

}

