		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate) || (!$selected_id && !$AllowInsert)){
		$jsReadOnly .= "\tjQuery('#team_logo').replaceWith('<div class=\"form-control-static\" id=\"team_logo\">' + (jQuery('#team_logo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#team_name').replaceWith('<div class=\"form-control-static\" id=\"team_name\">' + (jQuery('#team_name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#school').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#school_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#matches').replaceWith('<div class=\"form-control-static\" id=\"matches\">' + (jQuery('#matches').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#wins').replaceWith('<div class=\"form-control-static\" id=\"wins\">' + (jQuery('#wins').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#losses').replaceWith('<div class=\"form-control-static\" id=\"losses\">' + (jQuery('#losses').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#draws').replaceWith('<div class=\"form-control-static\" id=\"draws\">' + (jQuery('#draws').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#points').replaceWith('<div class=\"form-control-static\" id=\"points\">' + (jQuery('#points').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif(($AllowInsert && !$selected_id) || ($AllowUpdate && $selected_id)){
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(school)%%>', $combo_school->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(school)%%>', $combo_school->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(school)%%>', urlencode($combo_school->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'school' => array('schools', 'School'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(team_logo)%%>', ($noUploads ? '' : '<input type=hidden name=MAX_FILE_SIZE value=102400>'.$Translation['upload image'].' <input type="file" name="team_logo" id="team_logo">'), $templateCode);
	if($AllowUpdate && $row['team_logo'] != ''){
		$templateCode = str_replace('<%%REMOVEFILE(team_logo)%%>', '<br><input type="checkbox" name="team_logo_remove" id="team_logo_remove" value="1"> <label for="team_logo_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	}else{
		$templateCode = str_replace('<%%REMOVEFILE(team_logo)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(team_name)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(school)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(matches)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(wins)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(losses)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(draws)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(points)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_by)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(edited_by)%%>', '', $templateCode);

	// process values
	if($selected_id){
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$row['team_logo'] = ($row['team_logo'] != '' ? $row['team_logo'] : 'blank.gif');
		if( $dvprint) $templateCode = str_replace('<%%VALUE(team_logo)%%>', safe_html($urow['team_logo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(team_logo)%%>', html_attr($row['team_logo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(team_logo)%%>', urlencode($urow['team_logo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(team_name)%%>', safe_html($urow['team_name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(team_name)%%>', html_attr($row['team_name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(team_name)%%>', urlencode($urow['team_name']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(school)%%>', safe_html($urow['school']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(school)%%>', html_attr($row['school']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(school)%%>', urlencode($urow['school']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(matches)%%>', safe_html($urow['matches']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(matches)%%>', html_attr($row['matches']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(matches)%%>', urlencode($urow['matches']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(wins)%%>', safe_html($urow['wins']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(wins)%%>', html_attr($row['wins']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wins)%%>', urlencode($urow['wins']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(losses)%%>', safe_html($urow['losses']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(losses)%%>', html_attr($row['losses']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(losses)%%>', urlencode($urow['losses']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(draws)%%>', safe_html($urow['draws']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(draws)%%>', html_attr($row['draws']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(draws)%%>', urlencode($urow['draws']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(points)%%>', safe_html($urow['points']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(points)%%>', html_attr($row['points']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(points)%%>', urlencode($urow['points']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', safe_html($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%VALUE(edited_by)%%>', safe_html($urow['edited_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(edited_by)%%>', urlencode($urow['edited_by']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(team_logo)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(team_name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(team_name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(school)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(school)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(matches)%%>', '0', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(matches)%%>', urlencode('0'), $templateCode);
		$templateCode = str_replace('<%%VALUE(wins)%%>', '0', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(wins)%%>', urlencode('0'), $templateCode);
		$templateCode = str_replace('<%%VALUE(losses)%%>', '0', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(losses)%%>', urlencode('0'), $templateCode);
		$templateCode = str_replace('<%%VALUE(draws)%%>', '0', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(draws)%%>', urlencode('0'), $templateCode);
		$templateCode = str_replace('<%%VALUE(points)%%>', '0', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(points)%%>', urlencode('0'), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(edited_by)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(edited_by)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('teams');
	if($selected_id){
		$jdata = get_joined_record('teams', $selected_id);
		if($jdata === false) $jdata = get_defaults('teams');
		$rdata = $row;
	}
	$templateCode .= loadView('teams-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: teams_dv
	if(function_exists('teams_dv')){
		$args=array();
		teams_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>